mapper.update:
update t_course_info
course_name=#{name}, photo=#{photo}, course_hour=#{courseHour}, is_shaky=#{isShaky}, price=#{price}, course_state=#{courseState}, course_describe=#{courseDescribe}, start_time=#{startTime}, end_time=#{endTime}, school_name=#{schoolName}, shelf_time=#{shelfTime}, shelf_left_time=#{shelfLeftTime}, is_show=#{pure}, update_time=#{updateTime}
where course_id=#{courseId}

update t_course_info
  set is_delete=1
 where course_id=#{courseId}

update t_course_info
set update_time=#{updateTime}
where course_id=#{courseId}
