mapper.select:
select
from t_open_integral where is_delete=0
and integral_way like concat('%',#{integralWay},'%')
and is_show = #{pure}
order by open_integral_id asc limit #{dataNum},#{limit}

select count(1) from t_open_integral where is_delete=0
and integral_way like concat('%',#{name},'%')
and is_show = #{pure}

mapper.update:
update t_open_integral
set is_delete=1
where open_integral_id = #{integralId}

update t_open_integral
set update_time = #{updateTime}
where open_integral_id = #{integralId}

update t_open_integral
integral_way=#{integralWay}, integral_type=#{integralType}, sort=#{sort}, cut_url=#{cutUrl}, is_show=#{pure}, update_time=#{updateTime},
where open_integral_id = #{integralId}
