mapper.select:
select rotation_image_id rotationId, school_name schoolName, rotation_name rotationName, rotation_type rotationType, sort, cut_url cutUrl, is_show isShow, is_delete isDelete, create_time creatTime, update_time updateTime, from t_open_rotation where is_delete=0
and school_name like concat('%',#{name},'%')
order by rotation_image_id asc limit #{dataNum},#{limit}
