mapper.select:
select
from t_open_rotation where is_delete=0
and school_name like concat('%',#{schoolName},'%')
and is_show = #{pure}
order by rotation_image_id asc limit #{dataNum},#{limit}

select count(1) from t_open_rotation where is_delete=0
and school_name like concat('%',#{name},'%')
and is_show = #{pure}

mapper.update:
update t_open_rotation
set is_delete=1
where rotation_image_id = #{rotationId}

update t_open_rotation
set update_time = #{updateTime}
where rotation_image_id = #{rotationId}

update t_open_rotation
school_name=#{schoolName}, rotation_name=#{photo}, rotation_type=#{rotationType}, sort=#{sort}, cut_url=#{cutUrl}, is_show=#{pure}, update_time=#{updateTime},
where rotation_image_id = #{rotationId}
