mapper.select:
select

from t_open_we
where is_delete=0

    and is_show = #{pure}

order by open_we_id asc
limit #{dataNum},#{limit}

mapper.update:
update t_open_we
set is_delete=1
where open_we_id = #{weId}

update t_open_we
set update_time = #{updateTime}
where open_we_id = #{weId}

update t_open_we
we_photo=#{photo}, we_type=#{weType}, sort=#{sort}, cut_url=#{cutUrl}, is_show=#{pure}, update_time=#{updateTime},
where open_we_id = #{weId}
