mapper.update:
update t_student_info
student_name=#{studentName}, birthday=#{birthday}, gender=#{gender}, student_number=#{studentName}, photo=#{photo}, school_name=#{schoolName}, update_time=#{updateTime},
where student_id=#{studentId}

update t_student_info
set is_delete=1
where student_id=#{arg0}

update t_student_info
set update_time=#{updateTime}
where student_id=#{studentId}
